var ev3 = require('./ev3source/ev3.js');
var source = require('./ev3source/source.js');

// EV3 library
var objs = Object.getOwnPropertyNames(ev3);
for (var i in objs) {
	if (objs.hasOwnProperty(i)) {
		var val = objs[i];
		global['ev3_' + val] = ev3[val];
	}
}

// Source standard library
objs = Object.getOwnPropertyNames(source);
for (var i in objs) {
	if (objs.hasOwnProperty(i)) {
		var val = objs[i];
		global[val] = source[val];
	}
}

// Define all Math libraries
objs = Object.getOwnPropertyNames(Math);
for (var i in objs) {
	if (objs.hasOwnProperty(i)) {
		var val = objs[i];
		global['math_' + val] = Math[val];
	}
}
